import React, { useState } from 'react';
import './SizeVisualizer.css';

const SizeVisualizer = ({ skipOptions, selectedSkip, onSkipSelect }) => {
  const [hoveredSkip, setHoveredSkip] = useState(null);
  const [comparisonMode, setComparisonMode] = useState(false);

  // Calculate relative sizes based on the largest skip (40 yards)
  const getRelativeSize = (size) => {
    const maxSize = 40;
    const minDisplaySize = 20; // Minimum display percentage
    const maxDisplaySize = 100; // Maximum display percentage
    
    const sizeNum = parseInt(size.split(' ')[0]);
    const percentage = (sizeNum / maxSize) * 100;
    
    // Ensure minimum visibility
    return Math.max(minDisplaySize, percentage);
  };

  const getSkipDimensions = (size) => {
    const sizeNum = parseInt(size.split(' ')[0]);
    
    // Approximate real-world dimensions (simplified for visualization)
    const dimensionsMap = {
      4: { width: '6ft', height: '4ft', length: '8ft' },
      6: { width: '6ft', height: '4ft', length: '10ft' },
      8: { width: '6ft', height: '4ft', length: '12ft' },
      10: { width: '6ft', height: '5ft', length: '12ft' },
      12: { width: '6ft', height: '5ft', length: '14ft' },
      14: { width: '6ft', height: '6ft', length: '14ft' },
      16: { width: '6ft', height: '6ft', length: '16ft' },
      20: { width: '7ft', height: '7ft', length: '20ft' },
      40: { width: '8ft', height: '8ft', length: '22ft' }
    };
    
    return dimensionsMap[sizeNum] || { width: '6ft', height: '4ft', length: '8ft' };
  };

  return (
    <div className="size-visualizer">
      <div className="visualizer-controls">
        <button 
          className={`view-toggle ${!comparisonMode ? 'active' : ''}`}
          onClick={() => setComparisonMode(false)}
        >
          Scale View
        </button>
        <button 
          className={`view-toggle ${comparisonMode ? 'active' : ''}`}
          onClick={() => setComparisonMode(true)}
        >
          Comparison View
        </button>
      </div>

      <div className={`skip-visualizer-container ${comparisonMode ? 'comparison-mode' : 'scale-mode'}`}>
        {skipOptions.map((skip) => {
          const sizeNum = parseInt(skip.size.split(' ')[0]);
          const relativeSize = getRelativeSize(skip.size);
          const dimensions = getSkipDimensions(skip.size);
          const isSelected = selectedSkip === skip.id;
          const isHovered = hoveredSkip === skip.id;

          return (
            <div
              key={skip.id}
              className={`skip-visual-item ${isSelected ? 'selected' : ''} ${isHovered ? 'hovered' : ''}`}
              onClick={() => onSkipSelect(skip.id)}
              onMouseEnter={() => setHoveredSkip(skip.id)}
              onMouseLeave={() => setHoveredSkip(null)}
            >
              <div className="skip-info-header">
                <h3 className="skip-visual-name">{skip.name}</h3>
                <div className="skip-visual-price">{skip.price}</div>
              </div>

              <div 
                className="skip-visual-container"
                style={{ 
                  '--relative-size': comparisonMode ? '100%' : `${relativeSize}%`,
                  '--skip-color': `hsl(${sizeNum * 8}, 60%, 50%)`
                }}
              >
                <div className="skip-3d-visual">
                  <div className="skip-front-face">
                    <span className="skip-size-label">{skip.size}</span>
                  </div>
                  <div className="skip-top-face"></div>
                  <div className="skip-right-face"></div>
                </div>

                <div className="skip-dimensions">
                  <span>{dimensions.length} × {dimensions.width} × {dimensions.height}</span>
                </div>
              </div>

              <div className="skip-visual-details">
                <div className="skip-features-compact">
                  {skip.allowedOnRoad && (
                    <span className="feature-dot road-allowed" title="Road Permitted"></span>
                  )}
                  {skip.allowsHeavyWaste && (
                    <span className="feature-dot heavy-waste" title="Heavy Waste OK"></span>
                  )}
                </div>
                
                <div className="skip-hire-info">
                  <span className="hire-period">{skip.hirePeriod}</span>
                </div>
              </div>

              {(isHovered || isSelected) && (
                <div className="skip-tooltip">
                  <div className="tooltip-content">
                    <p><strong>Capacity:</strong> {skip.size}</p>
                    <p><strong>Dimensions:</strong> {dimensions.length} × {dimensions.width} × {dimensions.height}</p>
                    <p><strong>Price:</strong> {skip.price} (Inc. VAT)</p>
                    <p><strong>Hire Period:</strong> {skip.hirePeriod}</p>
                    {skip.allowedOnRoad && <p>✅ Road Permitted</p>}
                    {skip.allowsHeavyWaste && <p>✅ Heavy Waste Allowed</p>}
                  </div>
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default SizeVisualizer;
