import React from 'react';
import './SkipCard.css';

const SkipCard = ({ skip, isSelected, onSelect }) => {
  return (
    <div className={`skip-card ${isSelected ? 'selected' : ''}`} onClick={onSelect}>
      <div className="skip-image-container">
        {skip.image ? (
          <img 
            src={skip.image} 
            alt={skip.name}
            className="skip-image"
          />
        ) : (
          <div className="skip-placeholder">
            <div className="skip-icon-container">
              <span className="skip-icon">üóëÔ∏è</span>
              <div className="skip-size-text">{skip.size}</div>
            </div>
          </div>
        )}
        <div className="skip-size-badge">
          {skip.size}
        </div>
        {isSelected && <div className="selected-indicator">‚úì</div>}
      </div>

      <div className="skip-details">
        <div className="skip-header">
          <h3 className="skip-name">{skip.name}</h3>
          <div className="skip-pricing">
            <div className="skip-price">{skip.price}</div>
            <div className="price-includes-vat">Inc. VAT</div>
          </div>
        </div>

        <p className="skip-hire-period">{skip.hirePeriod}</p>

        <div className="skip-features">
          {skip.allowedOnRoad && (
            <span className="feature-badge road-allowed">
              <span className="badge-icon">üõ£Ô∏è</span>
              Road Permitted
            </span>
          )}
          {skip.allowsHeavyWaste && (
            <span className="feature-badge heavy-waste">
              <span className="badge-icon">‚öñÔ∏è</span>
              Heavy Waste OK
            </span>
          )}
        </div>

        <button 
          className={`select-button ${isSelected ? 'selected' : ''}`}
          onClick={(e) => {
            e.stopPropagation();
            onSelect();
          }}
        >
          {isSelected ? (
            <>
              <span className="button-icon">‚úì</span>
              Selected
            </>
          ) : (
            <>
              Select This Skip
              <span className="button-icon">‚Üí</span>
            </>
          )}
        </button>
      </div>
    </div>
  );
};

export default SkipCard;
