import React, { useRef } from 'react';
import SkipCard from './SkipCard';
import './SkipSelection.css';

const SkipSelection = ({ skipOptions, selectedSkip, onSkipSelect }) => {
  const carouselRef = useRef(null);

  const scrollLeft = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: -300, behavior: 'smooth' });
    }
  };

  const scrollRight = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: 300, behavior: 'smooth' });
    }
  };

  return (
    <div className="skip-selection">
      <div className="skip-selection-header">
        <h1 className="skip-selection-title">Choose Your Skip Size</h1>
        <p className="skip-selection-subtitle">
          Select the skip size that best suits your needs
        </p>
      </div>

      <div className="carousel-container">
        <button className="carousel-nav carousel-nav-left" onClick={scrollLeft}>
          ‹
        </button>

        <div className="skip-carousel" ref={carouselRef}>
          {skipOptions.map((skip) => (
            <div key={skip.id} className="carousel-item">
              <SkipCard
                skip={skip}
                isSelected={selectedSkip === skip.id}
                onSelect={() => onSkipSelect(skip.id)}
              />
            </div>
          ))}
        </div>

        <button className="carousel-nav carousel-nav-right" onClick={scrollRight}>
          ›
        </button>
      </div>

      <div className="carousel-indicators">
        {skipOptions.map((skip, index) => (
          <div 
            key={skip.id} 
            className={`indicator ${selectedSkip === skip.id ? 'active' : ''}`}
            onClick={() => {
              onSkipSelect(skip.id);
              if (carouselRef.current) {
                const cardWidth = 320; // Card width + gap
                carouselRef.current.scrollTo({ 
                  left: index * cardWidth, 
                  behavior: 'smooth' 
                });
              }
            }}
          >
            <span className="indicator-size">{skip.size.split(' ')[0]}</span>
          </div>
        ))}
      </div>

      {selectedSkip && (
        <div className="bottom-navigation">
          <div className="selected-skip-info">
            <span className="selected-skip-name">
              {skipOptions.find(s => s.id === selectedSkip)?.name}
            </span>
            <span className="selected-skip-price">
              {skipOptions.find(s => s.id === selectedSkip)?.price}
            </span>
            <span className="selected-skip-period">
              {skipOptions.find(s => s.id === selectedSkip)?.hirePeriod}
            </span>
          </div>
          <div className="navigation-buttons">
            <button className="back-button">Back</button>
            <button className="continue-button">Continue →</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SkipSelection;
