import React from 'react';
import './SkipCard.css';

const SkipCard = ({ skip, isSelected, onSelect }) => {
  return (
    <div className={`skip-card ${isSelected ? 'selected' : ''}`}>
      <div className="skip-image-container">
        {skip.image ? (
          <img 
            src={skip.image} 
            alt={skip.name}
            className="skip-image"
          />
        ) : (
          <div className="skip-placeholder">
            <span className="skip-icon">üóëÔ∏è</span>
          </div>
        )}
        <div className="skip-size-badge">
          {skip.size}
        </div>
      </div>

      <div className="skip-details">
        <h3 className="skip-name">{skip.name}</h3>
        <p className="skip-hire-period">{skip.hirePeriod}</p>

        <div className="skip-features">
          {skip.allowedOnRoad && (
            <span className="feature-badge road-allowed">Road Permitted</span>
          )}
          {skip.allowsHeavyWaste && (
            <span className="feature-badge heavy-waste">Heavy Waste OK</span>
          )}
        </div>

        <div className="skip-pricing">
          <div className="skip-price">{skip.price}</div>
          <div className="price-includes-vat">Inc. VAT</div>
        </div>

        <button 
          className={`select-button ${isSelected ? 'selected' : ''}`}
          onClick={onSelect}
        >
          {isSelected ? 'Selected' : 'Select This Skip ‚Üí'}
        </button>
      </div>
    </div>
  );
};

export default SkipCard;
