import React, { useState, useRef, useEffect } from 'react';
import SkipCard from './SkipCard';
import './SkipSelection.css';

const SkipSelection = ({ skipOptions, selectedSkip, onSkipSelect }) => {
  const [currentPage, setCurrentPage] = useState(0);
  const [cardsPerPage, setCardsPerPage] = useState(3);
  const scrollContainerRef = useRef(null);

  const totalPages = Math.ceil(skipOptions.length / cardsPerPage);

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        setCardsPerPage(1); // Mobile: 1 card per page
      } else if (window.innerWidth < 1024) {
        setCardsPerPage(2); // Tablet: 2 cards per page
      } else {
        setCardsPerPage(3); // Desktop: 3 cards per page
      }
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    setCurrentPage(0); // Reset to first page when cardsPerPage changes
  }, [cardsPerPage]);

  const nextPage = () => {
    setCurrentPage(prev => Math.min(prev + 1, totalPages - 1));
  };

  const prevPage = () => {
    setCurrentPage(prev => Math.max(prev - 1, 0));
  };

  const goToPage = (page) => {
    setCurrentPage(page);
  };

  const getCurrentPageSkips = () => {
    const startIndex = currentPage * cardsPerPage;
    const endIndex = startIndex + cardsPerPage;
    return skipOptions.slice(startIndex, endIndex);
  };

  return (
    <div className="skip-selection">
      <div className="skip-selection-header">
        <div className="header-content">
          <h1 className="skip-selection-title">
            Choose Your Perfect Skip
          </h1>
          <p className="skip-selection-subtitle">
            Select the skip size that perfectly matches your project needs
          </p>
          <div className="skip-count-badge">
            {skipOptions.length} Available Sizes
          </div>
        </div>
      </div>

      <div className="skip-carousel-container">
        <div className="carousel-header">
          <div className="page-indicator">
            <span className="current-page">{currentPage + 1}</span>
            <span className="page-divider">/</span>
            <span className="total-pages">{totalPages}</span>
          </div>

          <div className="carousel-controls">
            <button 
              className={`control-btn prev-btn ${currentPage === 0 ? 'disabled' : ''}`}
              onClick={prevPage}
              disabled={currentPage === 0}
            >
              ←
            </button>
            <button 
              className={`control-btn next-btn ${currentPage === totalPages - 1 ? 'disabled' : ''}`}
              onClick={nextPage}
              disabled={currentPage === totalPages - 1}
            >
              →
            </button>
          </div>
        </div>

        <div className="skip-carousel" ref={scrollContainerRef}>
          <div 
            className="skip-cards-container"
            style={{
              transform: `translateX(-${currentPage * 100}%)`,
              width: `${totalPages * 100}%`
            }}
          >
            {Array.from({ length: totalPages }, (_, pageIndex) => (
              <div key={pageIndex} className="page-container">
                {skipOptions
                  .slice(pageIndex * cardsPerPage, (pageIndex + 1) * cardsPerPage)
                  .map((skip) => (
                    <SkipCard
                      key={skip.id}
                      skip={skip}
                      isSelected={selectedSkip === skip.id}
                      onSelect={() => onSkipSelect(skip.id)}
                    />
                  ))}
              </div>
            ))}
          </div>
        </div>

        <div className="pagination-dots">
          {Array.from({ length: totalPages }, (_, index) => (
            <button
              key={index}
              className={`dot ${index === currentPage ? 'active' : ''}`}
              onClick={() => goToPage(index)}
            />
          ))}
        </div>
      </div>

      {selectedSkip && (
        <div className="floating-summary">
          <div className="summary-content">
            <div className="selected-skip-info">
              <div className="skip-details">
                <span className="selected-skip-name">
                  {skipOptions.find(s => s.id === selectedSkip)?.name}
                </span>
                <span className="selected-skip-period">
                  {skipOptions.find(s => s.id === selectedSkip)?.hirePeriod}
                </span>
              </div>
              <div className="selected-skip-price">
                {skipOptions.find(s => s.id === selectedSkip)?.price}
              </div>
            </div>
            <div className="action-buttons">
              <button className="back-button">
                ← Back
              </button>
              <button className="continue-button">
                Continue →
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SkipSelection;
